import React from 'react';

import { CSSTransition } from 'react-transition-group';


class Request extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: [],
          showList: false,
          highlightedHobby: false
        };
      }
    timedRequest(){
        let intervalId = setInterval(async () => {
            fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
              .then(res => res.json())
              .then(
                (results) => {
                  this.setState({
                    isLoaded: true,
                    result: results,
                    intervalId: intervalId

                              });
                },

                (error) => {
                  this.setState({
                    isLoaded: true,
                    error
                  });
                }
              )
              console.log(this.props.seconds)
            }, parseInt(this.props.seconds))
    }
      componentDidMount() {
          console.log("Mount")
          var newSec = parseInt(this.props.seconds)
          console.log({newSec});
          this.timedRequest();

      }
      componentDidUpdate(){
        console.log("UPDATE");
        clearInterval(this.state.intervalId)
        this.timedRequest();
      }
      switch = () => {
        this.setState(prevState => ({
          showList: !prevState.showList
        }));
      };
      listSwitch = () => {
        this.setState(state => ({
          highlightedHobby: !state.highlightedHobby
        }));
      };

      render() {
        const { error, isLoaded, result } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {

          return (
            <ul>
              {this.state.result.map((item, index) => (
                <li key={index}>
                    {this.state.result[index].from} - {this.state.result[index].to}
                    <CSSTransition
                        in={this.state.showList}
                        timeout={400}
                        classNames="list-transition"
                        unmountOnExit
                        appear
                        onEntered={this.listSwitch}
                        onExit={this.listSwitch}>
                    <div>
                      {this.state.result[index].rate}
                     </div>
                   </CSSTransition>
                    {/* {this.state.result[index].orderExpiresIn}
                    {this.state.result[index].status} */}
                </li>
              ))}
            </ul>
          );
        }
      }
    }


export default Request;
