import React from 'react';
import ReactCSSTransitionGroup from 'react-transition-group';


class Request extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
    timedRequest(){
        let intervalId = setInterval(async () => {
            fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
              .then(res => res.json())
              .then(
                (results) => {
                  this.setState({
                    isLoaded: true,
                    result: results,
                    intervalId: intervalId

                              });
                },

                (error) => {
                  this.setState({
                    isLoaded: true,
                    error
                  });
                }
              )
              console.log(this.props.seconds)
            }, parseInt(this.props.seconds))
    }
      componentDidMount() {
          console.log("Mount")
          var newSec = parseInt(this.props.seconds)
          console.log({newSec});
          this.timedRequest();

      }
      componentDidUpdate(){
        console.log("UPDATE");
        clearInterval(this.state.intervalId)
        this.timedRequest();
      }


      render() {
        const { error, isLoaded, result } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <ul>
              {this.state.result.map((item, index) => (
                <li key={index}>
                    {this.state.result[index].from} - {this.state.result[index].to}
                    <ReactCSSTransitionGroup
          transitionName="example"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={300}>
                   <div className="rate"> {this.state.result[index].rate}</div>
                   </ReactCSSTransitionGroup>

                    {/* {this.state.result[index].orderExpiresIn}
                    {this.state.result[index].status} */}
                </li>
              ))}
            </ul>
          );
        }
      }
    }


export default Request;
