import React from 'react';
import ReactCSSTransitionGroup from 'react-transition-group';


class Request extends React.Component{

    constructor(props) {
        super(props);
        this.listRef = React.createRef();

        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
    timedRequest(){
        let intervalId = setInterval(async () => {
            fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
              .then(res => res.json())
              .then(
                (results) => {
                  this.setState({
                    isLoaded: true,
                    items: results,
                    intervalId: intervalId

                              });
                },

                (error) => {
                  this.setState({
                    isLoaded: true,
                    error
                  });
                }
              )
              console.log(this.props.seconds)
            }, parseInt(this.props.seconds))
    }
    getSnapshotBeforeUpdate(prevProps, prevState) {
        // Are we adding new items to the list?
        // Capture the scroll position so we can adjust scroll later.
        console.log({prevState})
        if (prevState.items.length < this.state.items.length) {
          const list = this.listRef.current;
          return list;
        }
        return null;
      }
      componentDidMount() {
          console.log("Mount")
          var newSec = parseInt(this.props.seconds)
          console.log({newSec});
          this.timedRequest();

      }
      componentDidUpdate(prevProps, prevState, snapshot){
        console.log("UPDATE");
        clearInterval(this.state.intervalId)
        this.timedRequest();
      }


      render() {
        const { error, isLoaded, result } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <ul>
              {this.state.items.map((item, index) => (
                <li key={index}>
                    {this.state.items[index].from} - {this.state.items[index].to}

                   <div> {this.state.items[index].rate}</div>
                    {/* {this.state.result[index].orderExpiresIn}
                    {this.state.result[index].status} */}
                </li>
              ))}
            </ul>
          );
        }
      }
    }


export default Request;
