import React from 'react';
import ReactCSSTransitionGroup from 'react-transition-group';
import shortid from "shortid";


class Request extends React.Component{
    constructor(props) {
        super(props);
        this.listRef = React.createRef();
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
      getRandomKey = () => {
        return shortid.generate();
    }
    timedRequest(){
        let intervalId = setInterval(async () => {
            fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
              .then(res => res.json())
              .then(
                (results) => {
                  this.setState({
                    isLoaded: true,
                    items: results,
                    intervalId: intervalId
                              });
                },

                (error) => {
                  this.setState({
                    isLoaded: true,
                    error
                  });
                }
              )
            }, parseInt(this.props.seconds))
    }
    newRequest(time){
        setInterval(async () => {
            fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
              .then(res => res.json())
              .then(
                (results) => {
                  this.setState({
                    isLoaded: true,
                    items: results
                 });
                },

                (error) => {
                  this.setState({
                    isLoaded: true,
                    error
                  });
                }
              )
            }, time)
    }
      componentDidMount() {
          console.log("Mount")
          this.timedRequest();

      }
      componentDidUpdate(prevProps, prevState, snapshot){
        console.log("UPDATE");
        clearInterval(this.state.intervalId)
        var newSec = parseInt(this.props.seconds)

        this.newRequest(newSec);
      }


      render() {
          console.log("Rerender")
        const { error, isLoaded, result } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {

          return (
            <ul>
              {this.props.seconds}
              {this.state.items.map((item, index) => (
                <li key={this.getRandomKey()}>
                    {this.state.items[index].from} - {this.state.items[index].to}

                   <div className="rate"> {this.state.items[index].rate}</div>
                    {/* {this.state.result[index].orderExpiresIn}
                    {this.state.result[index].status} */}
                </li>
              ))}
            </ul>
          );
        }
      }
    }


export default Request;
