import React from 'react';


class Request extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }

      componentDidMount() {
          var newSec = parseInt(this.props.seconds)
          let intervalId = setInterval(async () => {
        fetch("https://liquality.io/swap/agent/api/swap/marketinfo")
          .then(res => res.json())
          .then(
            (results) => {
              this.setState({
                isLoaded: true,
                result: results,
                intervalId: intervalId

                          });
            },

            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
          console.log(this.props.seconds)
        }, parseInt(this.props.seconds))
      }
      componentDidUpdate(){
          console.log("unmount");
        clearInterval(this.state.intervalId)
      }


      render() {
        const { error, isLoaded, result } = this.state;
        if (error) {
          return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <ul>
              {this.state.result.map((item, index) => (
                <li  key={index}>
                    {this.state.result[index].from} - {this.state.result[index].to}
                   <div className="rate"> {this.state.result[index].rate}</div>

                </li>
              ))}
            </ul>
          );
        }
      }
    }


export default Request;
